import React, { useEffect, useState } from 'react'

function Intro() {
    const url=process.env.APIURL;
    const [data,setdata]=useState('');
    const func=async()=>{
        const res=await fetch(`${url}/Home/Intro`,{
            method:"GET",
            headers: {'Content-Type':'application/json'}
        });
        const dat=await res.json();
        console.log(dat);
        setdata(dat);
    }
    useEffect(()=>{
       func();
    },[])
  return (
    <div className="d-flex align-items-center justify-content-center min-vh-100 bg-dark bg-opacity-50">
            <div className="bg-white rounded-lg shadow-lg d-flex">
                <div className="p-4 w-100" style={{ maxWidth: '400px' }}>
                    <div className="d-flex justify-content-center mb-4">
                    <img src={require('frontend\src\assets\Shield_logo_of_Bukidnon_State_University.png').default} alt="BUKSU Logo" />
                    </div>
                    <h2 className="text-center mb-4">Welcome to BUKSU Hotel</h2>
                    <input type="text" placeholder="User name or Email" className="form-control mb-3" />
                    <input type="password" placeholder="Password" className="form-control mb-3" />
                    <button className="btn btn-primary btn-block mb-3">Log In</button>
                    <div className="text-center mb-3">Open with</div>
                    <button className="btn btn-primary btn-block d-flex align-items-center justify-content-center">
                        <i className="fab fa-google mr-2"></i> Google
                    </button>
                </div>
                <div className="p-4 w-100 d-flex align-items-center justify-content-center" style={{ maxWidth: '400px' }}>
                    <div className="text-center">
                    <img src={require('./assets/lgo.png').default} alt="BUKSU Hotel Logo" className="mb-4" />
                        <h2 className="mb-2">BUKSU HOTEL</h2>
                        <p>Laboratory</p>
                    </div>
                </div>
            </div>
        </div>
  )
}

export default Intro
